CREATE OR ALTER Procedure dbo.SD_spOrgLogo

(
--@userName varchar(100)=null,
@FileName nvarchar(500)=null,
@FileType nvarchar(500)=null,
@FileData varbinary (max) = NULL,
@Orgid nvarchar(max)=NULL,
