		BEGIN
			IF exists(select * from SD_OrgLogo where Org_ID=@Orgid)
				BEGIN
					update  SD_OrgLogo set FileName=@FileName,FileType=@FileType,FileData=@FileData where Org_ID=@Orgid
				END
