				BEGIN
					insert into SD_OrgLogo (FileName,FileType,FileData,Org_ID)values(@FileName,@FileType,@FileData,@Orgid) 
			
				END
		END
		if(@Option=''GetLogo'')
		BEGIN
		Select top 1  b.* from SD_User_Master a inner join 
                                                            SD_OrgLogo b 
                                                            on a.Org_ID=b.Org_ID
                                                            where a.Org_ID=@Orgid
		END
	
	END
