            SELECT '','' + CAST(HolidayDate AS NVARCHAR(10))
            FROM SD_Holidays
            WHERE OrgID = @OrgId
            FOR XML PATH(''''), TYPE).value(''.'', ''NVARCHAR(MAX)''), 1, 1, '''')
    );

