BEGIN
 Select @TotalRow=Count(id) from dbo.vSDTicket

Select * from (select ROW_NUMBER() OVER(order by CreationDate desc ) as RowNum,a.* ,case 
  when DATEDIFF(HOUR,CreationDate,GETDATE())<=1
