 when DATEDIFF(HOUR,CreationDate,GETDATE())<=24
 then ''Last24Hours''
 when DATEDIFF(DAY,CreationDate,GETDATE())> 1 and DATEDIFF(DAY,CreationDate,GETDATE())<= 30
