  End as ''TicketDayWise''
  from dbo.vSDTicket  a inner join 
    SD_ServDeskDefn b on a.ServiceDesk=b.Deskref 
	and a.OrgId=b.OrgFk
	where DATEDIFF(day,ClosedDate,GETDATE())>= b.autoArchiveTime and Status=''Closed'')tab
  
