   --      from dbo.vSD_Tickets with(nolock) where ServiceDesk=@ServiceDesk and OrgId=@OrgId  and CAST(CreationDate AS DATE) between  @FROM and  @TO and Status!=''Closed'' and Status!=''Resolved''         
   --      UNION        
