            AND CAST(CreationDate AS DATE) BETWEEN @FROM AND @TO 
            AND Status IN (''Closed'', ''Resolved'') 
    ) AS SourceTable          
PIVOT          
    (
        MAX([Ticket Count])          
        FOR Status IN ([Total], [Open], [Closed])          
    ) AS PivotTable;

        
        
