EXEC sp_executesql N'CREATE OR ALTER Proc dbo.SD_spSDMergeNupate  
(  
 @id numeric(26,0)= null  
 ,@DeskRef varchar(50) =NULL  
 ,@UserIDForAppro numeric(18,0)=null  
 ,@ticketout varchar(50)=Null  
    , @Ticketref varchar(20)= null   
