      ,@sdSeverityFK numeric(26,0)= null  
      ,@sdSolutionTypeFK numeric(26,0)= null  
      ,@sdCategoryFK numeric(26,0)= null  
      ,@creationDateTime datetime= null  
      ,@closedDateTime datetime= null  
