 if(@Option=''AddTicket'')  
  
    
         BEGIN  
              set @sdCategoryFK=(select top 1 id from SD_Category where Categoryref=@sdCategoryRef and OrgDeskRef=@organizationFK)  
 set @StageFKAsPerStatus=(select top 1 sd_stageFK from SD_Status where id=@sdStatusFK)  
   
