	DECLARE @deskref2 nvarchar(100);
	DECLARE @Severity1 numeric(26,0);
	DECLARE @OrgFk1 numeric(26,0);

	SET @HoursCovered = (SELECT HoursCovered FROM vSDOrgDeskDef WHERE Deskref = @DeskRef);
