        FROM dbo.vSD_Tickets WITH (NOLOCK) 
        WHERE 
            ServiceDesk = @ServiceDesk 
            AND OrgId = @OrgId  
            AND CAST(CreationDate AS DATE) BETWEEN @FROM AND @TO 
            AND Status NOT IN (''Closed'', ''Resolved'')         
        UNION        
        SELECT 
